assign_subsurface_abrasion <- function(eflalo, LE_MET) {
  eflalo$LE_SUB <-0
  eflalo$LE_SUB  <- ifelse(grepl("DRB", eflalo$LE_MET) & grepl("SMA", eflalo$LE_MET), 6.096, eflalo$LE_SUB)
