  eflalo$LE_SUB  <- ifelse(grepl("DRB", eflalo$LE_MET) & grepl("LAR", eflalo$LE_MET), 9.144, eflalo$LE_SUB)
  eflalo$LE_SUB  <- ifelse(grepl("TBB", eflalo$LE_MET) & grepl("SMA", eflalo$LE_MET), 8, eflalo$LE_SUB)
  eflalo$LE_SUB  <- ifelse(grepl("TBB", eflalo$LE_MET) & grepl("MED", eflalo$LE_MET), 14, eflalo$LE_SUB)
  eflalo$LE_SUB  <- ifelse(grepl("TBB", eflalo$LE_MET) & grepl("LAR", eflalo$LE_MET), 24, eflalo$LE_SUB)
  eflalo$LE_SUB  <- ifelse(grepl("OTB", eflalo$LE_MET) & grepl("SMA", eflalo$LE_MET), 1.52, eflalo$LE_SUB)
  eflalo$LE_SUB  <- ifelse(grepl("OTB", eflalo$LE_MET) & grepl("MED", eflalo$LE_MET), 2.13, eflalo$LE_SUB)
  eflalo$LE_SUB  <- ifelse(grepl("OTB", eflalo$LE_MET) & grepl("LAR", eflalo$LE_MET), 2.44, eflalo$LE_SUB)
  eflalo$LE_SUB  <- ifelse(grepl("OTT", eflalo$LE_MET) & grepl("SMA", eflalo$LE_MET), 2.13, eflalo$LE_SUB)
  eflalo$LE_SUB  <- ifelse(grepl("OTT", eflalo$LE_MET) & grepl("MED", eflalo$LE_MET), 2.74, eflalo$LE_SUB)
  eflalo$LE_SUB  <- ifelse(grepl("OTT", eflalo$LE_MET) & grepl("LAR", eflalo$LE_MET), 3.20, eflalo$LE_SUB)
  return(eflalo)}
